var kSmallestPairs = function (nums1, nums2, k) {
  var l1 = nums1.length, l2 = nums2.length, tmp;

  if (!l1 || !l2) return [];

  var heap = nums1.reduce(function (pre, n1) {
    pre = pre.concat(nums2.map(function (n2) {
      return [n1, n2];
    }));
    return pre;
  }, []);

  var result = [], count = Math.min(k, l1 * l2);
  while(count--) {
    for (var i = parseInt(heap.length / 2); i > 0; --i) {
      var mid = i - 1;
      var left = i * 2 - 1;
      var right = i * 2;

      if (heap[left] && heap[mid][0] + heap[mid][1] > heap[left][0] + heap[left][1]) {
        tmp = heap[left];
        heap[left] = heap[mid];
        heap[mid] = tmp;
      }

      if (heap[right] && heap[mid][0] + heap[mid][1] > heap[right][0] + heap[right][1]) {
        tmp = heap[right];
        heap[right] = heap[mid];
        heap[mid] = tmp;
      }
    }
    result.push(heap.shift());
  }

  return result;
};

